import {React, useState} from "react";
import './style.css';
import {FiUserPlus, FiCornerDownLeft} from 'react-icons/fi';
import { Link, useParams, useHistory } from "react-router-dom";
import api from "../../services/api";

export default function NovoAluno(){

    const [id, setId]= useState(null);
    const [nome, setNome]= useState('');
    const [email, setEmail]= useState('');
    const [idade, setIdade]= useState(0);

    const {alunoId} = useParams();
    const history = useHistory();

    const token = localStorage.getItem('token');
    const authorization = {
        headers : {
            Authorization: 'Bearer '+token,
        }
    }

    async function loadAluno(){
        try{
            const response =  await (await api.get(`api/aluno/${alunoId}`, authorization));

            setId(response.data.id);
        }catch(error){
            alert('falha '+ error);
            history.push('/alunos');
        }
    }

    return(
        <div className="novo-aluno-container">
            <div className="content">
                <section className="form">
                    <FiUserPlus size ='105' color="#17202a" />
                    <h1>{alunoId === '0' ? 'Incluir Novo Aluno': 'Atualizar Aluno'}</h1>
                    <Link className='back-link' to='/alunos'>
                        <FiCornerDownLeft size='23' color="#17202a" />
                        Retornar
                    </Link>
                </section>
                <form>
                    <input placeholder="Nome"/>
                    <input placeholder="Email"/>
                    <input placeholder="Idade"/>
                    <button className="button" type='submit'>
                        {alunoId === '0' ? 'Incluir': 'Atualizar'}
                    </button>
                </form>
            </div>
        </div>
    );
}
